insert into Materias values ("4006", "4000", "Seccion 4", "¿Cuál es la frase que engloba la idea principal del texto?", "Efectos positivos de los conciertos para disminuir el estrés", "Efectos de la música antes y después de escucharla", "Investigaciones con voluntarios para medir niveles hormonales", "Riesgos de padecer diabetes e importancia en los seres humanos");
